'use strict';

var logger = require('../lib/logger');
var _ = require('lodash');

var logLevels = ['info', 'debug', 'trace', 'error'];


describe('logger ', function () {

    beforeEach(function () {
    });

    it('supports common log levels', function () {
        logLevels.forEach(function (level) {
            expect(_.isBoolean(logger[level + 'Enabled'])).toBe(true);
            expect(_.isFunction(logger[level])).toBe(true);
        });
    });

    it('can enable loglevels at init', function () {
        logger.init('name', logLevels, ''.split(','));
        logLevels.forEach(function (level) {
            expect(logLevels.indexOf(level) !== -1).toBe(logger[level + 'Enabled']);
        });
    });

    it('will log error level by default', function () {
        logger.init('name', ''.split(','), ''.split(','));
        expect(logger.errorEnabled).toBe(true);
    });

});