var logger = require("./lib/logger");
logger.init("test", "debug,trace,info,error".split(","), "console,syslog,file".split(","));
logger.debugEnabled = true;
logger.info("Hello Info");
logger.debug("Hello debug", { process: process.argv });
logger.traceEnabled = true;
logger.trace("Hello trace", logger.IN);
logger.trace("Hello trace", logger.OUT);
logger.info("Hello Info");
logger.errorEnabled = true;
logger.error("Hello error");
logger.debug("Bye debug");
logger.debug("Hello debug", { process: process.env });
logger.trace("Hello trace", logger.IN);
logger.trace("Hello trace", logger.OUT);
logger.info("Hello Info");
logger.error("Hello error");
logger.info("Hello Info");
setInterval(function(){
	logger.info("Hello Info, Tick");
},5000);